# Write your MySQL query statement below
select Students.student_id,Students.student_name,Subjects.subject_name ,count(Examinations.subject_name)as attended_exams from Students cross join subjects left join examinations on students.student_id=examinations.student_id and subjects.subject_name=examinations.subject_name group by Subjects.subject_name,Students.student_name order by Students.student_id,subject_name;